
interface VConfType  {
    /**
     * vsicon图标组件默认配置
     */
    vsicon: {
        /**
         * 加载静态资源地址，默认为'https://cdn.c44.cc/vsicon'
         */
        url: string;
        /**
         * 使用的图标库名称，默认为'ant'
         */
        lib: string;
        /**
         * 使用的图标名称，默认为'up'
         */
        name: string;
        /**
         * 使用的图标大小，默认为'20'
         */
        size: any;
        /**
         * 使用的图标宽度，默认为空，取size字段
         */
        width: any;
        /**
         * 使用的图标高度，默认为空，取size字段
         */
        height: any;
        /**
         * 使用的图标颜色，默认为[]
         */
        color: string[];
        /**
         * 使用的图标单位，默认为px
         */
        unit: string;
        /**
         * 其他辅助方法
         */
        fun: {
            /**
             * 加载控制对象-外部无作用
             */
            load: any
            
            /**
             * 默认的内容缓存对象
             */
            resource: any
            /**
             * 保存资源-默认用的对象缓存
             * @param url 
             * @param content 
             * @returns 
             */
            setRes: (url: string, content: any) => void

            /**
             * 获取缓存资源
             * @param url 
             * @returns 
             */
            getRes: (url: string) => any

            /**
             * 获取图标资源
             * @param _url 如https://cdn.c44.cc/vsicon/ant/up.svg
             * @param save 是否对获取到的资源进行缓存，默认为false
             * @returns
             */
            getResorce: (_url: string,save?:boolean) => Promise<any>
        }
    };
    /**
     * vicon图标组件默认配置
     */
    vicon: {
        /**
         * 加载静态资源地址，默认为'https://cdn.c44.cc/vicon'
         */
        url: string;
        /**
         * 使用的图标库名称，默认为'ant'
         */
        lib: string;
        /**
         * 使用的图标名称，默认为'up'
         */
        name: string;
        /**
         * 使用的图标大小，默认为'20'
         */
        size: any;
        /**
         * 使用的图标颜色，默认为'currentColor'
         */
        color: any;
        /**
         * 如果为true，使用设计的图标颜色使得color属性无效。默认为false，一般用于多色或固定色图标
         */
        nofill: boolean;
        /**
         * 使用的图标单位，默认为px
         */
        unit: string;
        /**
         * 其他辅助方法
         */
        fun: {
            /**
             * 加载控制对象-外部无作用
             */
            load: any
            
            /**
             * 默认的内容缓存对象
             */
            resource: any
            /**
             * 保存资源-默认用的对象缓存
             * @param url 
             * @param content 
             * @returns 
             */
            setRes: (url: string, content: any) => void

            /**
             * 获取缓存资源
             * @param url 
             * @returns 
             */
            getRes: (url: string) => any

            /**
             * 获取图标资源
             * @param _url 如https://cdn.c44.cc/vicon/ant/up.json
             * @param save 是否对获取到的资源进行缓存，默认为false
             * @returns
             */
            getResorce: (_url: string,save?:boolean) => Promise<any>
        }
    };
    /**
     * 层级管理
     */
    zindex: {
        mask: number;
    }
}

declare var VConf: VConfType;